import React,{useState} from 'react';

function BookingForm() {
    var Acuity = require('acuityscheduling');
    var userId = null;
    var apiKey = null;
    var acuity = Acuity.basic({
        userId: 24946863,
        apiKey: "1c2fd73f6c3d6f4ffbca41af48fce8f7"
      });
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const [message, setMessage] = useState("");


    function handleSubmit(event) {
        console.log(name +  email + phone +  message)
        acuity.request('/appointments', function (err, res, appointments) {
            if (err) return console.error(err);
              console.log(appointments);
              console.log("appointments");
          });
      event.preventDefault();
      //console.log(event.target.value)
    }
   
    return (
        <>
            <form onSubmit={handleSubmit} className="form bookingForm mt-4">

                     <div className="form-group">
                    <label for="Name">Name</label>
                    <input required
                        type="text"
                        name="Name"
                        className="Name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        tabIndex="1"
                    />
                    </div>
                    <div className="form-group">
                    <label for="email">Email</label>
                    <input required
                        name="email"
                        id="email"
                        className="email"
                        tabIndex="2"
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    </div>
                    <div className="form-group">
                <label for="Phone">Phone no.</label>
                <input required
                    type="text"
                    name="Phone"
                    className="Phone"
                    tabIndex="3"
                    value={phone}
                    onChange={(e) => setPhone(e.target.value)}
                />
                </div>
                <div className="form-group">
                <label for="message">Notes</label>
                <textarea required
                    className="message"
                    name="message"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                />
                </div>
                <button type="submit" className="send btn w-100 btn_white">Book</button>
            </form>
        </>
    )
}

export default BookingForm;
