import React from 'react';


var _script, _scriptIsLoaded = false;

function placesTypeahead(element, apiKey) {
	  if (!element) {
			_error('you must a DOM input element as first parameter');
		}
    if (!_script) {
        if (!apiKey) {
           _error('you must provide an Google Maps API key as second parameter');
        }
        _script = document.createElement('script');
        document.body.appendChild(_script);
        _script.src = 'https://maps.googleapis.com/maps/api/js?key=' + apiKey + '&libraries=places';
        _script.async = true;
			  _script.addEventListener('load', function() {
					_scriptIsLoaded = true;
				});
    }
		_initWhenLoaded(element);																				
}
	
function _error(message) {
    throw new Error('PlacesTypeahead Error: ' + message);		
}			
	
function _initWhenLoaded(element) {
	if (_scriptIsLoaded) {
		_init(element);
	} else {
		_script.addEventListener('load', function() {
			_init(element);
		});
	}
}	

function _init(element) {
	var autocomplete = new google.maps.places.Autocomplete(element, { types: ['geocode']});
	autocomplete.addListener('place_changed', function() {
		var addressComponents = autocomplete.getPlace().address_components;
		var data = {
			country: _extractComponent(addressComponents, 'country'),
			state: _extractComponent(addressComponents, 'administrative_area_level_1'),
			city: _extractComponent(addressComponents, 'locality'),
			postal_code: _extractComponent(addressComponents, 'postal_code'),
			street: _extractComponent(addressComponents, 'route'),
			street_number: _extractComponent(addressComponents, 'street_number')
		};
		element.dispatchEvent(new CustomEvent('place_changed_custom', { detail: data }));
	});
}
													 
function _extractComponent(components, type) {
    return components.reduce(function (prev, curr) {
        if (prev) {
            return prev;
        }
        var indexOfType = curr.types.indexOf(type);
        return indexOfType !== -1 ? curr : null;
    }, null);
}

document.addEventListener('DOMContentLoaded', function () {
	var input = document.querySelector('.places-input');
	var output = document.querySelector('.output');
	document.body.appendChild(output);
	placesTypeahead(input, 'AIzaSyBWpKHiyXYW4A4K789n0hTBNLJB-N1bmIE');
	input.addEventListener('place_changed_custom', function(e) {
		output.innerHTML += JSON.stringify(e.detail);
	});
});

function Search() {
  return (
    <input type='text' placeholder='Enter your location...' class='places-input'/>
  );
}

export default Search;
