import React,{useState} from 'react';

function BookingForm() {

    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const [message, setMessage] = useState("");


    function handleSubmit(event) {
       // console.log(name +  email + phone +  message);
       var month  = localStorage.getItem('month');
       var numberofcloth  = localStorage.getItem('numberofcloth');
       var day = localStorage.getItem('day');
       var year  = new Date().getFullYear()
       var newAddress = localStorage.getItem('newAddress');
      
        var axios = require('axios');
        var FormData = require('form-data');
        var data = new FormData();
        data.append('datetime', '');
        data.append('firstName', name);
        data.append('lastName', name);
        data.append('phone', phone);
        data.append('quantity', '');
        data.append('notes', message);
        data.append('email', email);
        data.append('location', '');

        var config = {
        method: 'post',
        url: 'https://fortecsalesforce.com/laundary/book.php',
        headers: { 
            ...data.getHeaders()
        },
        data : data
        };

        axios(config)
        .then(function (response) {
        console.log(JSON.stringify(response.data));
        })
        .catch(function (error) {
        console.log(error);
        });

         event.preventDefault();
     
    }
   
    return (
        <>
            <form onSubmit={handleSubmit} className="form bookingForm mt-4">

                     <div className="form-group">
                    <label for="Name">Name</label>
                    <input required
                        type="text"
                        name="Name"
                        className="Name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        tabIndex="1"
                    />
                    </div>
                    <div className="form-group">
                    <label for="email">Email</label>
                    <input required
                        name="email"
                        id="email"
                        className="email"
                        tabIndex="2"
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    </div>
                    <div className="form-group">
                <label for="Phone">Phone no.</label>
                <input required
                    type="text"
                    name="Phone"
                    className="Phone"
                    tabIndex="3"
                    value={phone}
                    onChange={(e) => setPhone(e.target.value)}
                />
                </div>
                <div className="form-group">
                <label for="message">Notes</label>
                <textarea 
                    className="message"
                    name="message"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                />
                </div>
                <button type="submit" className="send btn w-100 btn_white">Book</button>
            </form>
        </>
    )
}

export default BookingForm;
